import React, { Component } from "react";
import NewsItem from "./NewsItem";
import "./globalClass";
import Spinner from "./Spinner";

export class News extends Component {
  constructor() {
    super();
    console.log("I am constructor from News");
    this.state = {
      articles: [],
      page: 1,
      totalResults: 0,
      loading: false,
    };
  }

  async componentDidMount() {
    let LUrl = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=3e9e7838c5544ecf80621f19694f5022
      &page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let LData = await fetch(LUrl);
    let LParsedata = await LData.json();
    this.setState({ loading: false });
    let LArticals = LParsedata.articles;
    LArticals = this.validateArray(LArticals);

    this.setState({
      articles: LArticals,
      totalResults: LParsedata.totalResults,
    });
  }
  handleOnNext = async () => {
    if (
      this.state.page + 1 >
      Math.ceil(this.state.totalResults / this.props.pageSize)
    ) {
      return;
    }

    let LUrl = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=3e9e7838c5544ecf80621f19694f5022
    &page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let LData = await fetch(LUrl);
    let LParsedata = await LData.json();
    this.setState({ loading: false });
    let LArticals = LParsedata.articles;
    LArticals = this.validateArray(LArticals);

    if (LArticals.length <= 0) {
      return;
    }

    this.setState({
      page: this.state.page + 1,
      articles: LArticals,
    });
  };

  handleOnPrevious = async () => {
    let LUrl = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=3e9e7838c5544ecf80621f19694f5022
    &page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true });
    let LData = await fetch(LUrl);
    let LParsedata = await LData.json();
    this.setState({ loading: false });
    let LArticals = LParsedata.articles;
    LArticals = this.validateArray(LArticals);

    this.setState({
      page: this.state.page - 1,
      articles: LArticals,
    });
  };

  validateArray(p_arrJson) {
    let LResultArray = [];
    for (let itemObject of p_arrJson) {
      if (
        itemObject.title &&
        itemObject.description &&
        itemObject.urlToImage &&
        itemObject.url
      ) {
        LResultArray.push(itemObject);
      }
    }
    return LResultArray;
  }

  fetchArtialFromServer() {
  }

  render() {
    console.log("rendered");
    return (
      <div className="container my-3">
        <h1 className="text-center">News Monkey - Top Headline</h1>
        {this.state.loading && <Spinner />}
        <div className="row">
          {this.state.articles.map((item) => {
            return (
              <div className="col-md-4" key={item.url}>
                <NewsItem
                  title={item.title ? item.title.slice(0, 40) : ""}
                  description={
                    item.description ? item.description.slice(0, 88) : ""
                  }
                  imageUrl={item.urlToImage ? item.urlToImage : ""}
                  newsUrl={item.url ? item.url : ""}
                />
              </div>
            );
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            type="button"
            disabled={this.state.page <= 1}
            className="btn btn-dark"
            onClick={this.handleOnPrevious}
          >
            &larr; Previous
          </button>
          <button
            type="button"
            disabled={this.state.articles.length <= 0}
            className="btn btn-dark"
            onClick={this.handleOnNext}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
