import React, { Component } from "react";
import NewsItem from "./NewsItem";
import "./globalClass";

export class News extends Component {

  constructor() {
    super();
    console.log("I am constructor from News");
    this.state = {
      articles: [],
      page:1,
      loading: false
    };
  }

  async componentDidMount(){
      let LUrl = "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=3e9e7838c5544ecf80621f19694f5022&page=1";
      let LData = await fetch(LUrl);
      let LParsedata = await LData.json();
      let LArticals = LParsedata.articles;
      LArticals = this.validateArray(LArticals);
      this.setState(
        {articles: LArticals}
      );
  }

  validateArray(p_arrJson) {
      let LResultArray = [];
      for(let itemObject of p_arrJson){
        if( itemObject.title && itemObject.description && itemObject.urlToImage && itemObject.url){
            LResultArray.push(itemObject);
        }
      }
      return LResultArray
  }

  handleOnPageChange(p_boolIsNextClick){

  }

  render() {
      console.log('rendered')
    return (
      <div className="container my-3">
        <h1>News Monkey - Top Headline </h1>
        <div className="row">
          {this.state.articles.map((item) => {
            return (
              <div className="col-md-4" key={(item.url)}>
                <NewsItem
                  title={ item.title ? item.title.slice(0,40) : ""}
                  description={item.description ? item.description.slice(0,88):""}
                  imageUrl={item.urlToImage ? item.urlToImage : ""}
                  newsUrl={item.url ? item.url : ""}
                />
              </div>
            );
          })}
        </div>
        <div className="container d-flex justify-content-between">
        <button type="button" disabled = {this.state.page<=1} className="btn btn-dark" onClick={this.handleOnPageChange(false)}>&larr; Previous</button>
        <button type="button" className="btn btn-dark" onClick={this.handleOnPageChange(true)}>Next &rarr;</button>
        </div>
      </div>
    );
  }
}

export default News;
