import React, { Component } from "react";
import NewsItem from "./NewsItem";
import "./globalClass";
import Spinner from "./Spinner";
import PropTypes from 'prop-types';
import eventBus from "../utility/EventBus";

export class News extends Component {

  static defaultProps = {
    pageSize:20,
    country:'in',
    category:'business'
  }

  static propsType ={
    pageSize:PropTypes.number,
    country: PropTypes.string,
    category:PropTypes.string
  }

  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      page: 1,
      totalResults: 0,
      loading: false,
    };

    let category = this.props.category[0].toUpperCase() + this.props.category.slice(1);
    document.title = `${category} - News Monkey`;
    console.log('constructore called')
  }

  componentDidMount() {

    console.log('componentDidMount called')
    
    eventBus.on("couponApply", (data) =>{
      console.log('Event Listned successfully.')
    });

    this.fetchNewsDataFromServer().then(LParsedata=>{

      let LArticals = LParsedata.articles;
      LArticals = this.validateArray(LArticals);
  
      this.setState({
        articles: LArticals,
        totalResults: LParsedata.totalResults,
      });
  
    })
  }

  handleOnNext = () => {
    if (
      this.state.page + 1 >
      Math.ceil(this.state.totalResults / this.props.pageSize)
    ) {
      return;
    }

    this.fetchNewsDataFromServer().then(LParsedata=>{

      let LArticals = LParsedata.articles;
      LArticals = this.validateArray(LArticals);
  
      if (LArticals.length <= 0) {
        return;
      }
  
      this.setState({
        page: this.state.page + 1,
        articles: LArticals,
      });
    })

  };

  handleOnPrevious = async () => {

    this.fetchNewsDataFromServer().then(LParsedata=>{
      
    let LArticals = LParsedata.articles;
    LArticals = this.validateArray(LArticals);

    this.setState({
      page: this.state.page - 1,
      articles: LArticals,
    });
    })

  };

  async fetchNewsDataFromServer() {
    
    let LUrl = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=3e9e7838c5544ecf80621f19694f5022
    &page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
    
    this.setState({ loading: true });
    let LData = await fetch(LUrl);
    let LParsedata = await LData.json();
    this.setState({ loading: false });

    return LParsedata;
  }

  validateArray(p_arrJson) {
    let LResultArray = [];
    for (let itemObject of p_arrJson) {
      if (
        itemObject.title &&
        itemObject.description &&
        itemObject.urlToImage &&
        itemObject.url
      ) {
        LResultArray.push(itemObject);
      }
    }
    return LResultArray;
  }
  render() {
    console.log('render called')
    return (
      <div className="container my-3">
        <h1 className="text-center">News Monkey - Top {this.props.category[0].toUpperCase() + this.props.category.slice(1)} Headline</h1>
        {this.state.loading && <Spinner />}
        <div className="row">
          { !this.state.loading &&this.state.articles.map((item) => {
            return (
              <div className="col-md-4" key={item.url}>
                <NewsItem
                  title={item.title ? item.title.slice(0, 40) : ""}
                  description={ item.description ? item.description.slice(0, 88) : ""}
                  imageUrl={item.urlToImage ? item.urlToImage : ""}
                  newsUrl={item.url ? item.url : ""}
                  publishedAt={item.publishedAt ? item.publishedAt : ""}
                  author = {item.author? item.author : ""}
                  source = {item.source? item.source : ""}
                />
              </div>
            );
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            type="button"
            disabled={this.state.page <= 1}
            className="btn btn-dark"
            onClick={this.handleOnPrevious}
          >
            &larr; Previous
          </button>
          <button
            type="button"
            disabled={this.state.articles.length <= 0}
            className="btn btn-dark"
            onClick={this.handleOnNext}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
